import { useMainStore } from '@/stores'
import LunBo from '@/components/LunBo.vue'
function toView(view) {
  console.log(view)
  router.push(view).then(() => {
    if (view === '/' || view === '/vip' || view === '/free') {
      return
    }
    // refresh page
    window.location.reload()
  })
}
const mainStore = useMainStore() // 获取
const props = defineProps(['type'])
const movieList = ref([])
watch(
  props,
  async (newProps) => {
    // 请求主页数据
    const data = await mainStore.getMovieList(newProps.type)
    console.log('bainl', data)
    movieList.value = data
  },
  {
    immediate: true,
  },
)

onMounted(() => {
  // 请求主页数据
  // console.log(props.type)
})
